	sql('update `ERP` set       `DocconNumber`=' . (($data['DocconNumber'] !== '' && $data['DocconNumber'] !== NULL) ? "'{$data['DocconNumber']}'" : 'NULL') . ', `BaseLocation`=' . (($data['BaseLocation'] !== '' && $data['BaseLocation'] !== NULL) ? "'{$data['BaseLocation']}'" : 'NULL') . ', `fo_DocItem`=' . (($data['fo_DocItem'] !== '' && $data['fo_DocItem'] !== NULL) ? "'{$data['fo_DocItem']}'" : 'NULL') . ', `fo_DocumentDescription`=' . (($data['fo_DocumentDescription'] !== '' && $data['fo_DocumentDescription'] !== NULL) ? "'{$data['fo_DocumentDescription']}'" : 'NULL') . ', `fo_Classification`=' . (($data['fo_Classification'] !== '' && $data['fo_Classification'] !== NULL) ? "'{$data['fo_Classification']}'" : 'NULL') . ', `fo_Impact`=' . (($data['fo_Impact'] !== '' && $data['fo_Impact'] !== NULL) ? "'{$data['fo_Impact']}'" : 'NULL') . ', `fo_Registerdate`=' . (($data['fo_Registerdate'] !== '' && $data['fo_Registerdate'] !== NULL) ? "'{$data['fo_Registerdate']}'" : 'NULL') . ', `ot_FileLoc`=' . (($data['ot_FileLoc'] !== '' && $data['ot_FileLoc'] !== NULL) ? "'{$data['ot_FileLoc']}'" : 'NULL') . ', `ot_otherdetails`=' . (($data['ot_otherdetails'] !== '' && $data['ot_otherdetails'] !== NULL) ? "'{$data['ot_otherdetails']}'" : 'NULL') . ', `ot_comments`=' . (($data['ot_comments'] !== '' && $data['ot_comments'] !== NULL) ? "'{$data['ot_comments']}'" : 'NULL') . ', `ot_SharedLink1`=' . (($data['ot_SharedLink1'] !== '' && $data['ot_SharedLink1'] !== NULL) ? "'{$data['ot_SharedLink1']}'" : 'NULL') . ', `ot_SharedLink2`=' . (($data['ot_SharedLink2'] !== '' && $data['ot_SharedLink2'] !== NULL) ? "'{$data['ot_SharedLink2']}'" : 'NULL') . ', ' . ($data['ot_Ref01']!='' ? "`ot_Ref01`='{$data['ot_Ref01']}'" : ($_REQUEST['ot_Ref01_remove'] != 1 ? '`ot_Ref01`=`ot_Ref01`' : '`ot_Ref01`=NULL')) . ', ' . ($data['ot_Ref02']!='' ? "`ot_Ref02`='{$data['ot_Ref02']}'" : ($_REQUEST['ot_Ref02_remove'] != 1 ? '`ot_Ref02`=`ot_Ref02`' : '`ot_Ref02`=NULL')) . ', ' . ($data['ot_Photo']!='' ? "`ot_Photo`='{$data['ot_Photo']}'" : ($_REQUEST['ot_Photo_remove'] != 1 ? '`ot_Photo`=`ot_Photo`' : '`ot_Photo`=NULL')) . ', `ot_ap_Review`=' . (($data['ot_ap_Review'] !== '' && $data['ot_ap_Review'] !== NULL) ? "'{$data['ot_ap_Review']}'" : 'NULL') . ', `ot_ap_RevComment`=' . (($data['ot_ap_RevComment'] !== '' && $data['ot_ap_RevComment'] !== NULL) ? "'{$data['ot_ap_RevComment']}'" : 'NULL') . ', `ot_ap_Approval`=' . (($data['ot_ap_Approval'] !== '' && $data['ot_ap_Approval'] !== NULL) ? "'{$data['ot_ap_Approval']}'" : 'NULL') . ', `ot_ap_ApprComment`=' . (($data['ot_ap_ApprComment'] !== '' && $data['ot_ap_ApprComment'] !== NULL) ? "'{$data['ot_ap_ApprComment']}'" : 'NULL') . ', `ot_ap_QC`=' . (($data['ot_ap_QC'] !== '' && $data['ot_ap_QC'] !== NULL) ? "'{$data['ot_ap_QC']}'" : 'NULL') . ', `ot_ap_QCComment`=' . (($data['ot_ap_QCComment'] !== '' && $data['ot_ap_QCComment'] !== NULL) ? "'{$data['ot_ap_QCComment']}'" : 'NULL') . ', `ot_ap_filed`=`ot_ap_filed`' . ', `ot_ap_lastmodified`=' . "'{$data['ot_ap_lastmodified']}'" . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="ERP_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: ERP_after_update
	if(function_exists('ERP_after_update')){
		$res = sql("SELECT * FROM `ERP` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!ERP_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='ERP' and pkValue='".makeSafe($selected_id)."'", $eo);

}

function ERP_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = ''){
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm=getTablePermissions('ERP');
	if(!$arrPerm[1] && $selected_id==''){ return ''; }
