	$combo_fo_Classification->MultipleSeparator = ', ';
	$combo_fo_Classification->ListBoxHeight = 10;
	$combo_fo_Classification->RadiosPerLine = 1;
	if(is_file(dirname(__FILE__).'/hooks/ERP.fo_Classification.csv')){
		$fo_Classification_data = addslashes(implode('', @file(dirname(__FILE__).'/hooks/ERP.fo_Classification.csv')));
		$combo_fo_Classification->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions($fo_Classification_data)));
		$combo_fo_Classification->ListData = $combo_fo_Classification->ListItem;
	}else{
		$combo_fo_Classification->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions("Emergency Response;;Contingency Response")));
		$combo_fo_Classification->ListData = $combo_fo_Classification->ListItem;
	}
	$combo_fo_Classification->SelectName = 'fo_Classification';
	$combo_fo_Classification->AllowNull = false;
	// combobox: fo_Impact
	$combo_fo_Impact = new Combo;
	$combo_fo_Impact->ListType = 0;
	$combo_fo_Impact->MultipleSeparator = ', ';
	$combo_fo_Impact->ListBoxHeight = 10;
