	// combobox: fo_Registerdate
	$combo_fo_Registerdate = new DateCombo;
	$combo_fo_Registerdate->DateFormat = "mdy";
	$combo_fo_Registerdate->MinYear = 1900;
	$combo_fo_Registerdate->MaxYear = 2100;
	$combo_fo_Registerdate->DefaultDate = parseMySQLDate('', '');
	$combo_fo_Registerdate->MonthNames = $Translation['month names'];
	$combo_fo_Registerdate->NamePrefix = 'fo_Registerdate';
	// combobox: ot_ap_Review
	$combo_ot_ap_Review = new DataCombo;
	// combobox: ot_ap_Approval
