		$combo_ot_ap_QC->SelectedData = $row['ot_ap_QC'];
		$row['ot_ap_filed'] = sqlValue("select DATE_FORMAT(`ot_ap_filed`, '%c/%e/%Y %l:%i%p') from `ERP` where `id`='".makeSafe($selected_id)."'");
		$row['ot_ap_lastmodified'] = sqlValue("select DATE_FORMAT(`ot_ap_lastmodified`, '%c/%e/%Y %l:%i%p') from `ERP` where `id`='".makeSafe($selected_id)."'");
	}else{
		$combo_BaseLocation->SelectedData = $filterer_BaseLocation;
		$combo_fo_Classification->SelectedText = ( $_REQUEST['FilterField'][1]=='6' && $_REQUEST['FilterOperator'][1]=='<=>' ? (get_magic_quotes_gpc() ? stripslashes($_REQUEST['FilterValue'][1]) : $_REQUEST['FilterValue'][1]) : "Unknown");
		$combo_fo_Impact->SelectedText = ( $_REQUEST['FilterField'][1]=='7' && $_REQUEST['FilterOperator'][1]=='<=>' ? (get_magic_quotes_gpc() ? stripslashes($_REQUEST['FilterValue'][1]) : $_REQUEST['FilterValue'][1]) : "Unknown");
		$combo_ot_ap_Review->SelectedData = $filterer_ot_ap_Review;
		$combo_ot_ap_Approval->SelectedData = $filterer_ot_ap_Approval;
		$combo_ot_ap_QC->SelectedData = $filterer_ot_ap_QC;
