	$combo_BaseLocation->HTML = '<span id="BaseLocation-container' . $rnd1 . '"></span><input type="hidden" name="BaseLocation" id="BaseLocation' . $rnd1 . '" value="' . html_attr($combo_BaseLocation->SelectedData) . '">';
