	$combo_ot_ap_Approval->HTML = $combo_ot_ap_Approval->MatchText = '<span id="ot_ap_Approval-container' . $rnd1 . '"></span>';
	$combo_ot_ap_QC->HTML = $combo_ot_ap_QC->MatchText = '<span id="ot_ap_QC-container' . $rnd1 . '"></span>';

	ob_start();
	?>

	<script>
