		AppGini.current_ot_ap_Approval__RAND__ = { text: "", value: "<?php echo addslashes($selected_id ? $urow['ot_ap_Approval'] : $filterer_ot_ap_Approval); ?>"};
		AppGini.current_ot_ap_QC__RAND__ = { text: "", value: "<?php echo addslashes($selected_id ? $urow['ot_ap_QC'] : $filterer_ot_ap_QC); ?>"};

		jQuery(function() {
