	);
	$templateCode .= loadView('ERP-ajax-cache', $cache_data);

	// hook: ERP_dv
	if(function_exists('ERP_dv')){
		$args=array();
		ERP_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
