function batches_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('batches');
	if(!$arrPerm[1]){
		return false;
	}

	$data['batch_no'] = makeSafe($_REQUEST['batch_no']);
		if($data['batch_no'] == empty_lookup_value){ $data['batch_no'] = ''; }
	$data['fo_item'] = makeSafe($_REQUEST['fo_item']);
		if($data['fo_item'] == empty_lookup_value){ $data['fo_item'] = ''; }
	$data['fo_suppliers'] = makeSafe($_REQUEST['fo_suppliers']);
		if($data['fo_suppliers'] == empty_lookup_value){ $data['fo_suppliers'] = ''; }
	$data['fo_manudate'] = intval($_REQUEST['fo_manudateYear']) . '-' . intval($_REQUEST['fo_manudateMonth']) . '-' . intval($_REQUEST['fo_manudateDay']);
	$data['fo_manudate'] = parseMySQLDate($data['fo_manudate'], '1');
	$data['fo_expdate'] = intval($_REQUEST['fo_expdateYear']) . '-' . intval($_REQUEST['fo_expdateMonth']) . '-' . intval($_REQUEST['fo_expdateDay']);
	$data['fo_expdate'] = parseMySQLDate($data['fo_expdate'], '1');
	$data['fo_Quantity'] = makeSafe($_REQUEST['fo_Quantity']);
		if($data['fo_Quantity'] == empty_lookup_value){ $data['fo_Quantity'] = ''; }
	$data['ot_FileLoc'] = makeSafe($_REQUEST['ot_FileLoc']);
		if($data['ot_FileLoc'] == empty_lookup_value){ $data['ot_FileLoc'] = ''; }
	$data['fo_otherdetails'] = br2nl(makeSafe($_REQUEST['fo_otherdetails']));
	$data['fo_comments'] = makeSafe($_REQUEST['fo_comments']);
		if($data['fo_comments'] == empty_lookup_value){ $data['fo_comments'] = ''; }
	$data['fo_SharedLink1'] = makeSafe($_REQUEST['fo_SharedLink1']);
		if($data['fo_SharedLink1'] == empty_lookup_value){ $data['fo_SharedLink1'] = ''; }
	$data['fo_SharedLink2'] = makeSafe($_REQUEST['fo_SharedLink2']);
		if($data['fo_SharedLink2'] == empty_lookup_value){ $data['fo_SharedLink2'] = ''; }
	$data['fo_ap_filed'] = parseCode('<%%creationDateTime%%>', true, true);
	$data['fo_Ref01'] = PrepareUploadedFile('fo_Ref01', 65536000,'txt|doc|docx|docm|odt|pdf|rtf', true, '');
	$data['fo_Ref02'] = PrepareUploadedFile('fo_Ref02', 65536000,'zip|rar|gz|tar|iso', true, '');
	$data['fo_Photo'] = PrepareUploadedFile('fo_Photo', 5120000,'jpg|jpeg|gif|png', false, '');
	if($data['fo_Photo']) createThumbnail($data['fo_Photo'], getThumbnailSpecs('batches', 'fo_Photo', 'tv'));
	if($data['batch_no']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Batch code': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from batches where id='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)){
			if(!$data['fo_Ref01']) $data['fo_Ref01'] = makeSafe($row['fo_Ref01']);
			if(!$data['fo_Ref02']) $data['fo_Ref02'] = makeSafe($row['fo_Ref02']);
			if(!$data['fo_Photo']) $data['fo_Photo'] = makeSafe($row['fo_Photo']);
		}
	}

	// hook: batches_before_insert
	if(function_exists('batches_before_insert')){
		$args=array();
		if(!batches_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `batches` set       `batch_no`=' . (($data['batch_no'] !== '' && $data['batch_no'] !== NULL) ? "'{$data['batch_no']}'" : 'NULL') . ', `fo_item`=' . (($data['fo_item'] !== '' && $data['fo_item'] !== NULL) ? "'{$data['fo_item']}'" : 'NULL') . ', `fo_suppliers`=' . (($data['fo_suppliers'] !== '' && $data['fo_suppliers'] !== NULL) ? "'{$data['fo_suppliers']}'" : 'NULL') . ', `fo_manudate`=' . (($data['fo_manudate'] !== '' && $data['fo_manudate'] !== NULL) ? "'{$data['fo_manudate']}'" : 'NULL') . ', `fo_expdate`=' . (($data['fo_expdate'] !== '' && $data['fo_expdate'] !== NULL) ? "'{$data['fo_expdate']}'" : 'NULL') . ', `fo_Quantity`=' . (($data['fo_Quantity'] !== '' && $data['fo_Quantity'] !== NULL) ? "'{$data['fo_Quantity']}'" : 'NULL') . ', `ot_FileLoc`=' . (($data['ot_FileLoc'] !== '' && $data['ot_FileLoc'] !== NULL) ? "'{$data['ot_FileLoc']}'" : 'NULL') . ', `fo_otherdetails`=' . (($data['fo_otherdetails'] !== '' && $data['fo_otherdetails'] !== NULL) ? "'{$data['fo_otherdetails']}'" : 'NULL') . ', `fo_comments`=' . (($data['fo_comments'] !== '' && $data['fo_comments'] !== NULL) ? "'{$data['fo_comments']}'" : 'NULL') . ', `fo_SharedLink1`=' . (($data['fo_SharedLink1'] !== '' && $data['fo_SharedLink1'] !== NULL) ? "'{$data['fo_SharedLink1']}'" : 'NULL') . ', `fo_SharedLink2`=' . (($data['fo_SharedLink2'] !== '' && $data['fo_SharedLink2'] !== NULL) ? "'{$data['fo_SharedLink2']}'" : 'NULL') . ', ' . ($data['fo_Ref01'] != '' ? "`fo_Ref01`='{$data['fo_Ref01']}'" : '`fo_Ref01`=NULL') . ', ' . ($data['fo_Ref02'] != '' ? "`fo_Ref02`='{$data['fo_Ref02']}'" : '`fo_Ref02`=NULL') . ', ' . ($data['fo_Photo'] != '' ? "`fo_Photo`='{$data['fo_Photo']}'" : '`fo_Photo`=NULL') . ', `fo_ap_filed`=' . "'{$data['fo_ap_filed']}'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"batches_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: batches_after_insert
	if(function_exists('batches_after_insert')){
		$res = sql("select * from `batches` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!batches_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('batches', $recID, getLoggedMemberID());

	return $recID;
}

function batches_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('batches');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='batches' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='batches' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: batches_before_delete
	if(function_exists('batches_before_delete')){
		$args=array();
		if(!batches_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// delete file stored in the 'fo_Ref01' field
	$res = sql("select `fo_Ref01` from `batches` where `id`='$selected_id'", $eo);
	if($row=@db_fetch_row($res)){
		if($row[0]!=''){
			@unlink(getUploadDir('').$row[0]);
		}
	}

	// delete file stored in the 'fo_Ref02' field
	$res = sql("select `fo_Ref02` from `batches` where `id`='$selected_id'", $eo);
	if($row=@db_fetch_row($res)){
		if($row[0]!=''){
			@unlink(getUploadDir('').$row[0]);
		}
	}

	// delete file stored in the 'fo_Photo' field
	$res = sql("select `fo_Photo` from `batches` where `id`='$selected_id'", $eo);
	if($row=@db_fetch_row($res)){
		if($row[0]!=''){
			@unlink(getUploadDir('').$row[0]);
			preg_match('/^[a-z0-9_]+\.(gif|png|jpg|jpeg|jpe)$/i', $row[0], $m);
			$thumbDV=str_replace(".$m[1]ffffgggg", "_dv.$m[1]", $row[0].'ffffgggg');
			$thumbTV=str_replace(".$m[1]ffffgggg", "_tv.$m[1]", $row[0].'ffffgggg');
			@unlink(getUploadDir('').$thumbTV);
			@unlink(getUploadDir('').$thumbDV);
		}
	}

	sql("delete from `batches` where `id`='$selected_id'", $eo);

	// hook: batches_after_delete
	if(function_exists('batches_after_delete')){
		$args=array();
		batches_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='batches' and pkValue='$selected_id'", $eo);
}

