function batches_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = ''){
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm=getTablePermissions('batches');
	if(!$arrPerm[1] && $selected_id==''){ return ''; }
	$AllowInsert = ($arrPerm[1] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != ''){
		$dvprint = true;
	}

	$filterer_fo_item = thisOr(undo_magic_quotes($_REQUEST['filterer_fo_item']), '');
	$filterer_fo_suppliers = thisOr(undo_magic_quotes($_REQUEST['filterer_fo_suppliers']), '');

	// populate filterers, starting from children to grand-parents

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: fo_item
	$combo_fo_item = new DataCombo;
	// combobox: fo_suppliers
	$combo_fo_suppliers = new DataCombo;
	// combobox: fo_manudate
	$combo_fo_manudate = new DateCombo;
	$combo_fo_manudate->DateFormat = "mdy";
	$combo_fo_manudate->MinYear = 1900;
	$combo_fo_manudate->MaxYear = 2100;
	$combo_fo_manudate->DefaultDate = parseMySQLDate('1', '1');
	$combo_fo_manudate->MonthNames = $Translation['month names'];
	$combo_fo_manudate->NamePrefix = 'fo_manudate';
	// combobox: fo_expdate
	$combo_fo_expdate = new DateCombo;
	$combo_fo_expdate->DateFormat = "mdy";
	$combo_fo_expdate->MinYear = 1900;
	$combo_fo_expdate->MaxYear = 2100;
	$combo_fo_expdate->DefaultDate = parseMySQLDate('1', '1');
	$combo_fo_expdate->MonthNames = $Translation['month names'];
	$combo_fo_expdate->NamePrefix = 'fo_expdate';

	if($selected_id){
		// mm: check member permissions
		if(!$arrPerm[2]){
			return "";
		}
		// mm: who is the owner?
		$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='batches' and pkValue='".makeSafe($selected_id)."'");
		$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='batches' and pkValue='".makeSafe($selected_id)."'");
		if($arrPerm[2]==1 && getLoggedMemberID()!=$ownerMemberID){
			return "";
		}
		if($arrPerm[2]==2 && getLoggedGroupID()!=$ownerGroupID){
			return "";
		}

		// can edit?
		if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){
			$AllowUpdate=1;
		}else{
			$AllowUpdate=0;
		}

		$res = sql("select * from `batches` where `id`='".makeSafe($selected_id)."'", $eo);
		if(!($row = db_fetch_array($res))){
			return error_message($Translation['No records found'], 'batches_view.php', false);
		}
		$urow = $row; /* unsanitized data */
		$hc = new CI_Input();
		$row = $hc->xss_clean($row); /* sanitize data */
		$combo_fo_item->SelectedData = $row['fo_item'];
		$combo_fo_suppliers->SelectedData = $row['fo_suppliers'];
		$combo_fo_manudate->DefaultDate = $row['fo_manudate'];
		$combo_fo_expdate->DefaultDate = $row['fo_expdate'];
		$row['fo_ap_filed'] = sqlValue("select DATE_FORMAT(`fo_ap_filed`, '%c/%e/%Y %l:%i%p') from `batches` where `id`='".makeSafe($selected_id)."'");
		$row['fo_lastmodified'] = sqlValue("select DATE_FORMAT(`fo_lastmodified`, '%c/%e/%Y %l:%i%p') from `batches` where `id`='".makeSafe($selected_id)."'");
	}else{
		$combo_fo_item->SelectedData = $filterer_fo_item;
		$combo_fo_suppliers->SelectedData = $filterer_fo_suppliers;
	}
