function categories_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('categories');
	if(!$arrPerm[1]){
		return false;
	}

	$data['CategoryName'] = makeSafe($_REQUEST['CategoryName']);
		if($data['CategoryName'] == empty_lookup_value){ $data['CategoryName'] = ''; }
	$data['fo_Description'] = makeSafe($_REQUEST['fo_Description']);
		if($data['fo_Description'] == empty_lookup_value){ $data['fo_Description'] = ''; }
	$data['ot_FileLoc'] = makeSafe($_REQUEST['ot_FileLoc']);
		if($data['ot_FileLoc'] == empty_lookup_value){ $data['ot_FileLoc'] = ''; }
	$data['ot_otherdetails'] = br2nl(makeSafe($_REQUEST['ot_otherdetails']));
	$data['ot_comments'] = makeSafe($_REQUEST['ot_comments']);
		if($data['ot_comments'] == empty_lookup_value){ $data['ot_comments'] = ''; }
	$data['ot_SharedLink1'] = makeSafe($_REQUEST['ot_SharedLink1']);
		if($data['ot_SharedLink1'] == empty_lookup_value){ $data['ot_SharedLink1'] = ''; }
	$data['ot_SharedLink2'] = makeSafe($_REQUEST['ot_SharedLink2']);
		if($data['ot_SharedLink2'] == empty_lookup_value){ $data['ot_SharedLink2'] = ''; }
	$data['ot_ap_filed'] = parseCode('<%%creationDateTime%%>', true, true);
	$data['ot_Ref01'] = PrepareUploadedFile('ot_Ref01', 65536000,'txt|doc|docx|docm|odt|pdf|rtf', true, '');
	$data['ot_Ref02'] = PrepareUploadedFile('ot_Ref02', 65536000,'zip|rar|gz|tar|iso', true, '');
	$data['ot_Picture'] = PrepareUploadedFile('ot_Picture', 5120000,'jpg|jpeg|gif|png', false, '');
	if($data['ot_Picture']) createThumbnail($data['ot_Picture'], getThumbnailSpecs('categories', 'ot_Picture', 'tv'));
	if($data['ot_Picture']) createThumbnail($data['ot_Picture'], getThumbnailSpecs('categories', 'ot_Picture', 'dv'));
	if($data['CategoryName']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Category Name': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from categories where CategoryID='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)){
			if(!$data['ot_Ref01']) $data['ot_Ref01'] = makeSafe($row['ot_Ref01']);
			if(!$data['ot_Ref02']) $data['ot_Ref02'] = makeSafe($row['ot_Ref02']);
			if(!$data['ot_Picture']) $data['ot_Picture'] = makeSafe($row['ot_Picture']);
		}
	}

	// hook: categories_before_insert
	if(function_exists('categories_before_insert')){
		$args=array();
		if(!categories_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `categories` set       `CategoryName`=' . (($data['CategoryName'] !== '' && $data['CategoryName'] !== NULL) ? "'{$data['CategoryName']}'" : 'NULL') . ', `fo_Description`=' . (($data['fo_Description'] !== '' && $data['fo_Description'] !== NULL) ? "'{$data['fo_Description']}'" : 'NULL') . ', `ot_FileLoc`=' . (($data['ot_FileLoc'] !== '' && $data['ot_FileLoc'] !== NULL) ? "'{$data['ot_FileLoc']}'" : 'NULL') . ', `ot_otherdetails`=' . (($data['ot_otherdetails'] !== '' && $data['ot_otherdetails'] !== NULL) ? "'{$data['ot_otherdetails']}'" : 'NULL') . ', `ot_comments`=' . (($data['ot_comments'] !== '' && $data['ot_comments'] !== NULL) ? "'{$data['ot_comments']}'" : 'NULL') . ', `ot_SharedLink1`=' . (($data['ot_SharedLink1'] !== '' && $data['ot_SharedLink1'] !== NULL) ? "'{$data['ot_SharedLink1']}'" : 'NULL') . ', `ot_SharedLink2`=' . (($data['ot_SharedLink2'] !== '' && $data['ot_SharedLink2'] !== NULL) ? "'{$data['ot_SharedLink2']}'" : 'NULL') . ', ' . ($data['ot_Ref01'] != '' ? "`ot_Ref01`='{$data['ot_Ref01']}'" : '`ot_Ref01`=NULL') . ', ' . ($data['ot_Ref02'] != '' ? "`ot_Ref02`='{$data['ot_Ref02']}'" : '`ot_Ref02`=NULL') . ', ' . ($data['ot_Picture'] != '' ? "`ot_Picture`='{$data['ot_Picture']}'" : '`ot_Picture`=NULL') . ', `ot_ap_filed`=' . "'{$data['ot_ap_filed']}'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"categories_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: categories_after_insert
	if(function_exists('categories_after_insert')){
		$res = sql("select * from `categories` where `CategoryID`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!categories_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('categories', $recID, getLoggedMemberID());

	return $recID;
}

function categories_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('categories');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='categories' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='categories' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: categories_before_delete
	if(function_exists('categories_before_delete')){
		$args=array();
		if(!categories_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: Item
	$res = sql("select `CategoryID` from `categories` where `CategoryID`='$selected_id'", $eo);
	$CategoryID = db_fetch_row($res);
	$rires = sql("select count(1) from `Item` where `fo_CategoryID`='".addslashes($CategoryID[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "Item", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "Item", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='categories_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='categories_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	// delete file stored in the 'ot_Ref01' field
	$res = sql("select `ot_Ref01` from `categories` where `CategoryID`='$selected_id'", $eo);
	if($row=@db_fetch_row($res)){
		if($row[0]!=''){
			@unlink(getUploadDir('').$row[0]);
		}
	}

	// delete file stored in the 'ot_Ref02' field
	$res = sql("select `ot_Ref02` from `categories` where `CategoryID`='$selected_id'", $eo);
	if($row=@db_fetch_row($res)){
		if($row[0]!=''){
			@unlink(getUploadDir('').$row[0]);
		}
	}

	// delete file stored in the 'ot_Picture' field
	$res = sql("select `ot_Picture` from `categories` where `CategoryID`='$selected_id'", $eo);
	if($row=@db_fetch_row($res)){
		if($row[0]!=''){
			@unlink(getUploadDir('').$row[0]);
			preg_match('/^[a-z0-9_]+\.(gif|png|jpg|jpeg|jpe)$/i', $row[0], $m);
			$thumbDV=str_replace(".$m[1]ffffgggg", "_dv.$m[1]", $row[0].'ffffgggg');
			$thumbTV=str_replace(".$m[1]ffffgggg", "_tv.$m[1]", $row[0].'ffffgggg');
			@unlink(getUploadDir('').$thumbTV);
			@unlink(getUploadDir('').$thumbDV);
		}
	}

	sql("delete from `categories` where `CategoryID`='$selected_id'", $eo);

	// hook: categories_after_delete
	if(function_exists('categories_after_delete')){
		$args=array();
		categories_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='categories' and pkValue='$selected_id'", $eo);
}

