		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		}else{
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	}else{
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#CategoryName').replaceWith('<div class=\"form-control-static\" id=\"CategoryName\">' + (jQuery('#CategoryName').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ot_FileLoc').replaceWith('<div class=\"form-control-static\" id=\"ot_FileLoc\">' + (jQuery('#ot_FileLoc').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ot_otherdetails').replaceWith('<div class=\"form-control-static\" id=\"ot_otherdetails\">' + (jQuery('#ot_otherdetails').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ot_SharedLink1').replaceWith('<div class=\"form-control-static\" id=\"ot_SharedLink1\">' + (jQuery('#ot_SharedLink1').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ot_SharedLink1, #ot_SharedLink1-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#ot_SharedLink2').replaceWith('<div class=\"form-control-static\" id=\"ot_SharedLink2\">' + (jQuery('#ot_SharedLink2').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ot_SharedLink2, #ot_SharedLink2-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#ot_Ref01').replaceWith('<div class=\"form-control-static\" id=\"ot_Ref01\">' + (jQuery('#ot_Ref01').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ot_Ref01, #ot_Ref01-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#ot_Ref02').replaceWith('<div class=\"form-control-static\" id=\"ot_Ref02\">' + (jQuery('#ot_Ref02').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ot_Ref02, #ot_Ref02-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#ot_Picture').replaceWith('<div class=\"form-control-static\" id=\"ot_Picture\">' + (jQuery('#ot_Picture').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array();
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(CategoryID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(CategoryName)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(fo_Description)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ot_FileLoc)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ot_otherdetails)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ot_comments)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ot_SharedLink1)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ot_SharedLink2)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ot_Ref01)%%>', ($noUploads ? '' : '<input type=hidden name=MAX_FILE_SIZE value=65536000>'.$Translation['upload image'].' <input type="file" name="ot_Ref01" id="ot_Ref01">'), $templateCode);
	if($AllowUpdate && $row['ot_Ref01'] != ''){
		$templateCode = str_replace('<%%REMOVEFILE(ot_Ref01)%%>', '<br><input type="checkbox" name="ot_Ref01_remove" id="ot_Ref01_remove" value="1"> <label for="ot_Ref01_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	}else{
		$templateCode = str_replace('<%%REMOVEFILE(ot_Ref01)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(ot_Ref02)%%>', ($noUploads ? '' : '<input type=hidden name=MAX_FILE_SIZE value=65536000>'.$Translation['upload image'].' <input type="file" name="ot_Ref02" id="ot_Ref02">'), $templateCode);
	if($AllowUpdate && $row['ot_Ref02'] != ''){
		$templateCode = str_replace('<%%REMOVEFILE(ot_Ref02)%%>', '<br><input type="checkbox" name="ot_Ref02_remove" id="ot_Ref02_remove" value="1"> <label for="ot_Ref02_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	}else{
		$templateCode = str_replace('<%%REMOVEFILE(ot_Ref02)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(ot_Picture)%%>', ($noUploads ? '' : '<input type=hidden name=MAX_FILE_SIZE value=5120000>'.$Translation['upload image'].' <input type="file" name="ot_Picture" id="ot_Picture">'), $templateCode);
	if($AllowUpdate && $row['ot_Picture'] != ''){
		$templateCode = str_replace('<%%REMOVEFILE(ot_Picture)%%>', '<br><input type="checkbox" name="ot_Picture_remove" id="ot_Picture_remove" value="1"> <label for="ot_Picture_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	}else{
		$templateCode = str_replace('<%%REMOVEFILE(ot_Picture)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(ot_ap_filed)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ot_ap_lastmodified)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(CategoryID)%%>', safe_html($urow['CategoryID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(CategoryID)%%>', html_attr($row['CategoryID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CategoryID)%%>', urlencode($urow['CategoryID']), $templateCode);
