		}else{
			$templateCode = str_replace('<%%HTMLAREA(fo_Description)%%>', '<div id="fo_Description" class="form-control-static">' . $row['fo_Description'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(fo_Description)%%>', nl2br($row['fo_Description']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(fo_Description)%%>', urlencode($urow['fo_Description']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ot_FileLoc)%%>', safe_html($urow['ot_FileLoc']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ot_FileLoc)%%>', html_attr($row['ot_FileLoc']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_FileLoc)%%>', urlencode($urow['ot_FileLoc']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)){
			$templateCode = str_replace('<%%VALUE(ot_otherdetails)%%>', safe_html($urow['ot_otherdetails']), $templateCode);
		}else{
			$templateCode = str_replace('<%%VALUE(ot_otherdetails)%%>', html_attr($row['ot_otherdetails']), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(ot_otherdetails)%%>', urlencode($urow['ot_otherdetails']), $templateCode);
		if($AllowUpdate || $AllowInsert){
			$templateCode = str_replace('<%%HTMLAREA(ot_comments)%%>', '<textarea name="ot_comments" id="ot_comments" rows="5">' . html_attr($row['ot_comments']) . '</textarea>', $templateCode);
		}else{
			$templateCode = str_replace('<%%HTMLAREA(ot_comments)%%>', '<div id="ot_comments" class="form-control-static">' . $row['ot_comments'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(ot_comments)%%>', nl2br($row['ot_comments']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_comments)%%>', urlencode($urow['ot_comments']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ot_SharedLink1)%%>', safe_html($urow['ot_SharedLink1']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ot_SharedLink1)%%>', html_attr($row['ot_SharedLink1']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_SharedLink1)%%>', urlencode($urow['ot_SharedLink1']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ot_SharedLink2)%%>', safe_html($urow['ot_SharedLink2']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ot_SharedLink2)%%>', html_attr($row['ot_SharedLink2']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_SharedLink2)%%>', urlencode($urow['ot_SharedLink2']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ot_Ref01)%%>', safe_html($urow['ot_Ref01']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ot_Ref01)%%>', html_attr($row['ot_Ref01']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_Ref01)%%>', urlencode($urow['ot_Ref01']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ot_Ref02)%%>', safe_html($urow['ot_Ref02']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ot_Ref02)%%>', html_attr($row['ot_Ref02']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_Ref02)%%>', urlencode($urow['ot_Ref02']), $templateCode);
		$row['ot_Picture'] = ($row['ot_Picture'] != '' ? $row['ot_Picture'] : 'blank.gif');
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ot_Picture)%%>', safe_html($urow['ot_Picture']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ot_Picture)%%>', html_attr($row['ot_Picture']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_Picture)%%>', urlencode($urow['ot_Picture']), $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_ap_filed)%%>', safe_html($urow['ot_ap_filed']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_ap_filed)%%>', urlencode($urow['ot_ap_filed']), $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_ap_lastmodified)%%>', safe_html($urow['ot_ap_lastmodified']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_ap_lastmodified)%%>', urlencode($urow['ot_ap_lastmodified']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(CategoryID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CategoryID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(CategoryName)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CategoryName)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(fo_Description)%%>', '<textarea name="fo_Description" id="fo_Description" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_FileLoc)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_FileLoc)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_otherdetails)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_otherdetails)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(ot_comments)%%>', '<textarea name="ot_comments" id="ot_comments" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_SharedLink1)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_SharedLink1)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_SharedLink2)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_SharedLink2)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_Ref01)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_Ref01)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_Ref02)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_Ref02)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_Picture)%%>', 'blank.gif', $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_ap_filed)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_ap_filed)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(ot_ap_lastmodified)%%>', '<%%editingDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ot_ap_lastmodified)%%>', urlencode('<%%editingDateTime%%>'), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
			$templateCode.="\n\tif(document.getElementById('ot_SharedLink1Edit')){ document.getElementById('ot_SharedLink1Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('ot_SharedLink1EditLink')){ document.getElementById('ot_SharedLink1EditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('ot_SharedLink2Edit')){ document.getElementById('ot_SharedLink2Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('ot_SharedLink2EditLink')){ document.getElementById('ot_SharedLink2EditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('ot_Ref01Edit')){ document.getElementById('ot_Ref01Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('ot_Ref01EditLink')){ document.getElementById('ot_Ref01EditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('ot_Ref02Edit')){ document.getElementById('ot_Ref02Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('ot_Ref02EditLink')){ document.getElementById('ot_Ref02EditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('categories');
	if($selected_id){
		$jdata = get_joined_record('categories', $selected_id);
		if($jdata === false) $jdata = get_defaults('categories');
		$rdata = $row;
	}
	$cache_data = array(
		'rdata' => array_map('nl2br', array_map('addslashes', $rdata)),
		'jdata' => array_map('nl2br', array_map('addslashes', $jdata))
	);
	$templateCode .= loadView('categories-ajax-cache', $cache_data);

	// hook: categories_dv
	if(function_exists('categories_dv')){
		$args=array();
		categories_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
