function ReportComment_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('ReportComment');
	if(!$arrPerm[1]){
		return false;
	}

	$data['PostID'] = makeSafe($_REQUEST['PostID']);
		if($data['PostID'] == empty_lookup_value){ $data['PostID'] = ''; }
	$data['TextPost'] = br2nl(makeSafe($_REQUEST['TextPost']));
	$data['filed'] = parseCode('<%%creationDateTime%%>', true, true);
	if($data['PostID'] == '') $data['PostID'] = "0";

	// hook: ReportComment_before_insert
	if(function_exists('ReportComment_before_insert')){
		$args=array();
		if(!ReportComment_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `ReportComment` set       `PostID`=' . (($data['PostID'] !== '' && $data['PostID'] !== NULL) ? "'{$data['PostID']}'" : 'NULL') . ', `TextPost`=' . (($data['TextPost'] !== '' && $data['TextPost'] !== NULL) ? "'{$data['TextPost']}'" : 'NULL') . ', `filed`=' . "'{$data['filed']}'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"ReportComment_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: ReportComment_after_insert
	if(function_exists('ReportComment_after_insert')){
		$res = sql("select * from `ReportComment` where `RunningID`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!ReportComment_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('ReportComment', $recID, getLoggedMemberID());

	return $recID;
}

