function ReportComment_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('ReportComment');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='ReportComment' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='ReportComment' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['PostID'] = makeSafe($_REQUEST['PostID']);
		if($data['PostID'] == empty_lookup_value){ $data['PostID'] = ''; }
	$data['TextPost'] = br2nl(makeSafe($_REQUEST['TextPost']));
	$data['last_modified'] = parseCode('<%%editingDateTime%%>', false, true);
	$data['selectedID']=makeSafe($selected_id);

	// hook: ReportComment_before_update
	if(function_exists('ReportComment_before_update')){
		$args=array();
		if(!ReportComment_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `ReportComment` set       `PostID`=' . (($data['PostID'] !== '' && $data['PostID'] !== NULL) ? "'{$data['PostID']}'" : 'NULL') . ', `TextPost`=' . (($data['TextPost'] !== '' && $data['TextPost'] !== NULL) ? "'{$data['TextPost']}'" : 'NULL') . ', `filed`=`filed`' . ', `last_modified`=' . "'{$data['last_modified']}'" . " where `RunningID`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="ReportComment_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: ReportComment_after_update
	if(function_exists('ReportComment_after_update')){
		$res = sql("SELECT * FROM `ReportComment` WHERE `RunningID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['RunningID'];
		$args = array();
		if(!ReportComment_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='ReportComment' and pkValue='".makeSafe($selected_id)."'", $eo);

}

