	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('vendor');
	if($selected_id){
		$jdata = get_joined_record('vendor', $selected_id);
		if($jdata === false) $jdata = get_defaults('vendor');
		$rdata = $row;
	}
	$templateCode .= loadView('vendor-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: vendor_dv
	if(function_exists('vendor_dv')){
		$args=array();
		vendor_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
