details`=' . (($data['ot_otherdetails'] !== '' && $data['ot_otherdetails'] !== NULL) ? "'{$data['ot_otherdetails']}'" : 'NULL') . ', `ot_comments`=' . (($data['ot_comments'] !== '' && $data['ot_comments'] !== NULL) ? "'{$data['ot_comments']}'" : 'NULL') . ', `ot_SharedLink1`=' . (($data['ot_SharedLink1'] !== '' && $data['ot_SharedLink1'] !== NULL) ? "'{$data['ot_SharedLink1']}'" : 'NULL') . ', `ot_SharedLink2`=' . (($data['ot_SharedLink2'] !== '' && $data['ot_SharedLink2'] !== NULL) ? "'{$data['ot_SharedLink2']}'" : 'NULL') . ', ' . ($data['ot_Ref01']!='' ? "`ot_Ref01`='{$data['ot_Ref01']}'" : ($_REQUEST['ot_Ref01_remove'] != 1 ? '`ot_Ref01`=`ot_Ref01`' : '`ot_Ref01`=NULL')) . ', ' . ($data['ot_Ref02']!='' ? "`ot_Ref02`='{$data['ot_Ref02']}'" : ($_REQUEST['ot_Ref02_remove'] != 1 ? '`ot_Ref02`=`ot_Ref02`' : '`ot_Ref02`=NULL')) . ', ' . ($data['ot_Photo']!='' ? "`ot_Photo`='{$data['ot_Photo']}'" : ($_REQUEST['ot_Photo_remove'] != 1 ? '`ot_Photo`=`ot_Photo`' : '`ot_Photo`=NULL')) . ', `ot_ap_Review`=' . (($data['ot_ap_Review'] !== '' && $data['ot_ap_Review'] !== NULL) ? "'{$data['ot_ap_Review']}'" : 'NULL') . ', `ot_ap_RevComment`=' . (($data['ot_ap_RevComment'] !== '' && $data['ot_ap_RevComment'] !== NULL) ? "'{$data['ot_ap_RevComment']}'" : 'NULL') . ', `ot_ap_Approval`=' . (($data['ot_ap_Approval'] !== '' && $data['ot_ap_Approval'] !== NULL) ? "'{$data['ot_ap_Approval']}'" : 'NULL') . ', `ot_ap_ApprComment`=' . (($data['ot_ap_ApprComment'] !== '' && $data['ot_ap_ApprComment'] !== NULL) ? "'{$data['ot_ap_ApprComment']}'" : 'NULL') . ', `ot_ap_QC`=' . (($data['ot_ap_QC'] !== '' && $data['ot_ap_QC'] !== NULL) ? "'{$data['ot_ap_QC']}'" : 'NULL') . ', `ot_ap_QCComment`=' . (($data['ot_ap_QCComment'] !== '' && $data['ot_ap_QCComment'] !== NULL) ? "'{$data['ot_ap_QCComment']}'" : 'NULL') . ', `ot_ap_filed`=`ot_ap_filed`' . ', `ot_ap_lastmodified`=' . "'{$data['ot_ap_lastmodified']}'" . " where `id`='".makeSafe($selected_id)."'", $o);
