	// hook: IMSControl_before_insert
	if(function_exists('IMSControl_before_insert')){
		$args=array();
		if(!IMSControl_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `IMSControl` set       `Status`=' . (($data['Status'] !== '' && $data['Status'] !== NULL) ? "'{$data['Status']}'" : 'NULL') . ', `Description`=' . (($data['Description'] !== '' && $data['Description'] !== NULL) ? "'{$data['Description']}'" : 'NULL') . ', `other_details`=' . (($data['other_details'] !== '' && $data['other_details'] !== NULL) ? "'{$data['other_details']}'" : 'NULL') . ', `filed`=' . "'{$data['filed']}'", $o);
