		echo $o['error'];
		echo "<a href=\"IMSControl_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: IMSControl_after_insert
	if(function_exists('IMSControl_after_insert')){
		$res = sql("select * from `IMSControl` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!IMSControl_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('IMSControl', $recID, getLoggedMemberID());

	return $recID;
}

