function TeamSoftBoard_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('TeamSoftBoard');
	if(!$arrPerm[1]){
		return false;
	}

	$data['Title'] = makeSafe($_REQUEST['Title']);
		if($data['Title'] == empty_lookup_value){ $data['Title'] = ''; }
	$data['TextPost'] = br2nl(makeSafe($_REQUEST['TextPost']));
	$data['website'] = makeSafe($_REQUEST['website']);
		if($data['website'] == empty_lookup_value){ $data['website'] = ''; }
	$data['filed'] = parseCode('<%%creationDateTime%%>', true, true);
	$data['image'] = PrepareUploadedFile('image', 5120000,'jpg|jpeg|gif|png', false, '');
	$data['Ref01'] = PrepareUploadedFile('Ref01', 65536000,'txt|doc|docx|docm|odt|pdf|rtf|zip', true, '');
	if($data['Title']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Post Title': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from TeamSoftBoard where Postid='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)){
			if(!$data['image']) $data['image'] = makeSafe($row['image']);
			if(!$data['Ref01']) $data['Ref01'] = makeSafe($row['Ref01']);
		}
	}

	// hook: TeamSoftBoard_before_insert
	if(function_exists('TeamSoftBoard_before_insert')){
		$args=array();
		if(!TeamSoftBoard_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `TeamSoftBoard` set       `Title`=' . (($data['Title'] !== '' && $data['Title'] !== NULL) ? "'{$data['Title']}'" : 'NULL') . ', ' . ($data['image'] != '' ? "`image`='{$data['image']}'" : '`image`=NULL') . ', `TextPost`=' . (($data['TextPost'] !== '' && $data['TextPost'] !== NULL) ? "'{$data['TextPost']}'" : 'NULL') . ', `website`=' . (($data['website'] !== '' && $data['website'] !== NULL) ? "'{$data['website']}'" : 'NULL') . ', ' . ($data['Ref01'] != '' ? "`Ref01`='{$data['Ref01']}'" : '`Ref01`=NULL') . ', `filed`=' . "'{$data['filed']}'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"TeamSoftBoard_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: TeamSoftBoard_after_insert
	if(function_exists('TeamSoftBoard_after_insert')){
		$res = sql("select * from `TeamSoftBoard` where `Postid`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!TeamSoftBoard_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('TeamSoftBoard', $recID, getLoggedMemberID());

	return $recID;
}

function TeamSoftBoard_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('TeamSoftBoard');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='TeamSoftBoard' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='TeamSoftBoard' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: TeamSoftBoard_before_delete
	if(function_exists('TeamSoftBoard_before_delete')){
		$args=array();
		if(!TeamSoftBoard_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: SoftboardComment
	$res = sql("select `Postid` from `TeamSoftBoard` where `Postid`='$selected_id'", $eo);
	$Postid = db_fetch_row($res);
	$rires = sql("select count(1) from `SoftboardComment` where `PostID`='".addslashes($Postid[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "SoftboardComment", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "SoftboardComment", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='TeamSoftBoard_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='TeamSoftBoard_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	// delete file stored in the 'Ref01' field
	$res = sql("select `Ref01` from `TeamSoftBoard` where `Postid`='$selected_id'", $eo);
	if($row=@db_fetch_row($res)){
		if($row[0]!=''){
			@unlink(getUploadDir('').$row[0]);
		}
	}

	sql("delete from `TeamSoftBoard` where `Postid`='$selected_id'", $eo);

	// hook: TeamSoftBoard_after_delete
	if(function_exists('TeamSoftBoard_after_delete')){
		$args=array();
		TeamSoftBoard_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='TeamSoftBoard' and pkValue='$selected_id'", $eo);
}

function TeamSoftBoard_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('TeamSoftBoard');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='TeamSoftBoard' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='TeamSoftBoard' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['Title'] = makeSafe($_REQUEST['Title']);
		if($data['Title'] == empty_lookup_value){ $data['Title'] = ''; }
	if($data['Title']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Post Title': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['TextPost'] = br2nl(makeSafe($_REQUEST['TextPost']));
	$data['website'] = makeSafe($_REQUEST['website']);
		if($data['website'] == empty_lookup_value){ $data['website'] = ''; }
	$data['last_modified'] = parseCode('<%%editingDateTime%%>', false, true);
	$data['selectedID']=makeSafe($selected_id);
	if($_REQUEST['image_remove'] == 1){
		$data['image'] = '';
	}else{
		$data['image'] = PrepareUploadedFile('image', 5120000, 'jpg|jpeg|gif|png', false, "");
	}
	if($_REQUEST['Ref01_remove'] == 1){
		$data['Ref01'] = '';
		// delete file from server
		$res = sql("select `Ref01` from `TeamSoftBoard` where `Postid`='".makeSafe($selected_id)."'", $eo);
		if($row=@db_fetch_row($res)){
			if($row[0]!=''){
				@unlink(getUploadDir('').$row[0]);
			}
		}
	}else{
		$data['Ref01'] = PrepareUploadedFile('Ref01', 65536000, 'txt|doc|docx|docm|odt|pdf|rtf|zip', true, "");
		// delete file from server
		if($data['Ref01'] != ''){
			$res = sql("select `Ref01` from `TeamSoftBoard` where `Postid`='".makeSafe($selected_id)."'", $eo);
			if($row=@db_fetch_row($res)){
				if($row[0]!=''){
					@unlink(getUploadDir('').$row[0]);
				}
			}
		}
	}

	// hook: TeamSoftBoard_before_update
	if(function_exists('TeamSoftBoard_before_update')){
		$args=array();
		if(!TeamSoftBoard_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `TeamSoftBoard` set       `Title`=' . (($data['Title'] !== '' && $data['Title'] !== NULL) ? "'{$data['Title']}'" : 'NULL') . ', ' . ($data['image']!='' ? "`image`='{$data['image']}'" : ($_REQUEST['image_remove'] != 1 ? '`image`=`image`' : '`image`=NULL')) . ', `TextPost`=' . (($data['TextPost'] !== '' && $data['TextPost'] !== NULL) ? "'{$data['TextPost']}'" : 'NULL') . ', `website`=' . (($data['website'] !== '' && $data['website'] !== NULL) ? "'{$data['website']}'" : 'NULL') . ', ' . ($data['Ref01']!='' ? "`Ref01`='{$data['Ref01']}'" : ($_REQUEST['Ref01_remove'] != 1 ? '`Ref01`=`Ref01`' : '`Ref01`=NULL')) . ', `filed`=`filed`' . ', `last_modified`=' . "'{$data['last_modified']}'" . " where `Postid`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="TeamSoftBoard_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: TeamSoftBoard_after_update
	if(function_exists('TeamSoftBoard_after_update')){
		$res = sql("SELECT * FROM `TeamSoftBoard` WHERE `Postid`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['Postid'];
		$args = array();
		if(!TeamSoftBoard_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='TeamSoftBoard' and pkValue='".makeSafe($selected_id)."'", $eo);

}

