		if(!$dvprint) $templateCode = str_replace('<%%VALUE(website)%%>', html_attr($row['website']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(website)%%>', urlencode($urow['website']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Ref01)%%>', safe_html($urow['Ref01']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Ref01)%%>', html_attr($row['Ref01']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Ref01)%%>', urlencode($urow['Ref01']), $templateCode);
		$templateCode = str_replace('<%%VALUE(filed)%%>', safe_html($urow['filed']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(filed)%%>', urlencode($urow['filed']), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_modified)%%>', safe_html($urow['last_modified']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_modified)%%>', urlencode($urow['last_modified']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(Postid)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Postid)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Title)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Title)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(image)%%>', 'blank.gif', $templateCode);
		$templateCode = str_replace('<%%VALUE(TextPost)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TextPost)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(website)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(website)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Ref01)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Ref01)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(filed)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(filed)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_modified)%%>', '<%%editingDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_modified)%%>', urlencode('<%%editingDateTime%%>'), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
			$templateCode.="\n\tif(document.getElementById('websiteEdit')){ document.getElementById('websiteEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('websiteEditLink')){ document.getElementById('websiteEditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('Ref01Edit')){ document.getElementById('Ref01Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('Ref01EditLink')){ document.getElementById('Ref01EditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('TeamSoftBoard');
	if($selected_id){
		$jdata = get_joined_record('TeamSoftBoard', $selected_id);
		if($jdata === false) $jdata = get_defaults('TeamSoftBoard');
		$rdata = $row;
	}
	$cache_data = array(
		'rdata' => array_map('nl2br', array_map('addslashes', $rdata)),
		'jdata' => array_map('nl2br', array_map('addslashes', $jdata))
	);
	$templateCode .= loadView('TeamSoftBoard-ajax-cache', $cache_data);

	// hook: TeamSoftBoard_dv
	if(function_exists('TeamSoftBoard_dv')){
		$args=array();
		TeamSoftBoard_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
